import { home } from '@/page/echart/index';
export default {
  name: 'home',
  components: {},
  data: function data() {
    return {
      chart1: {
        option: home.chart1
      },
      seled: '',
      sel: [{
        value: 'en-US',
        babel: 'English'
      }, {
        value: 'zh-CN',
        babel: '中文'
      }],
      user: '接口数据'
    };
  },
  created: function created() {
    var _this = this;

    // 处理图表的数据
    this.handleData(); // dom更新后才会执行里面的内容

    this.$nextTick(function () {
      // 图表挂载到dom元素上
      _this.chartInit('chart1');
    });
    this.$apis.home.queryUser().then(function (res) {
      _this.$utils.disposeAxiosData({
        response: res,
        success: function success(res) {
          console.log(res);
          _this.user = res.data.payload;
        }
      });
    });
  },
  mounted: function mounted() {
    // eslint-disable-next-line no-console
    console.log(this.$store.state.home.pageSize);
  },
  methods: {
    handleData: function handleData() {
      var xData = ['4/6', '4/7', '48/', '4/9', '4/10'];
      var sData = [10, 20, 50, 60, 63];
      this.chart1.option.xAxis.data = xData;
      this.chart1.option.series[0].data = sData;
    },
    chartInit: function chartInit(id) {
      console.log(id, this[id].option);
      var div = document.getElementById(id);
      var myChart = this.$echarts.init(div);
      myChart.setOption(this[id].option);
    },
    selLanguage: function selLanguage(val) {
      console.log(val);
      this.$i18n.locale = val;
      this.$store.dispatch('set_language', val);
      localStorage.setItem('language', val); // this.$forceUpdate();
    }
  }
};
